package PacoteOperadores;

public class ClasseModulo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// módulo (resto da divisão)
		
		double n1 = 0, n2 = 0, total = 0;
		n1 = 83;
		n2 = 4;
		total = n1 % n2;		
		System.out.printf("O número %.2f divido por %.2f possui resto %.2f.", n1, n2, total);
	}

}
